		lineDB.ID = 0
		query := backRepoLine.db.Create(lineDB)
		if query.Error != nil {
			log.Panic(query.Error)
		}
		(*backRepoLine.Map_LineDBID_LineDB)[lineDB.ID] = lineDB
		BackRepoLineid_atBckpTime_newID[lineDB_ID_atBackupTime] = lineDB.ID
