// generated by gong
import { Component, OnInit, Inject, Optional } from '@angular/core';
import { CdkDragDrop, moveItemInArray } from '@angular/cdk/drag-drop';

import { MatDialogRef, MAT_DIALOG_DATA, MatDialog } from '@angular/material/dialog'
import { DialogData } from '../front-repo.service'
import { SelectionModel } from '@angular/cdk/collections';

import { Router, RouterState } from '@angular/router';
import { VisualCircleDB } from '../visualcircle-db'
import { VisualCircleService } from '../visualcircle.service'

import { FrontRepoService, FrontRepo, NullInt64 } from '../front-repo.service'
@Component({
  selector: 'lib-visualcircle-sorting',
  templateUrl: './visualcircle-sorting.component.html',
  styleUrls: ['./visualcircle-sorting.component.css']
})
export class VisualCircleSortingComponent implements OnInit {

  frontRepo: FrontRepo

  // array of VisualCircle instances that are in the association
  associatedVisualCircles = new Array<VisualCircleDB>();

  constructor(
    private visualcircleService: VisualCircleService,
    private frontRepoService: FrontRepoService,

    // not null if the component is called as a selection component of visualcircle instances
    public dialogRef: MatDialogRef<VisualCircleSortingComponent>,
    @Optional() @Inject(MAT_DIALOG_DATA) public dialogData: DialogData,

    private router: Router,
  ) {
    this.router.routeReuseStrategy.shouldReuseRoute = function () {
      return false;
    };
  }

  ngOnInit(): void {
    this.getVisualCircles()
  }

  getVisualCircles(): void {
    this.frontRepoService.pull().subscribe(
      frontRepo => {
        this.frontRepo = frontRepo

        let index = 0
        for (let visualcircle of this.frontRepo.VisualCircles_array) {
          let ID = this.dialogData.ID
          let revPointerID = visualcircle[this.dialogData.ReversePointer]
          let revPointerID_Index = visualcircle[this.dialogData.ReversePointer+"_Index"]
          if (revPointerID.Int64 == ID) {
            if (revPointerID_Index == undefined) {
              revPointerID_Index = new NullInt64
              revPointerID_Index.Valid = true
              revPointerID_Index.Int64 = index++
            }
            this.associatedVisualCircles.push(visualcircle)
          }
        }

        // sort associated visualcircle according to order
        this.associatedVisualCircles.sort((t1, t2) => {
          let t1_revPointerID_Index = t1[this.dialogData.ReversePointer+"_Index"]
          let t2_revPointerID_Index = t2[this.dialogData.ReversePointer+"_Index"]
          if (t1_revPointerID_Index && t2_revPointerID_Index) {
            if (t1_revPointerID_Index.Int64 > t2_revPointerID_Index.Int64) {
              return 1;
            }
            if (t1_revPointerID_Index.Int64 < t2_revPointerID_Index.Int64) {
              return -1;
            }  
          }
          return 0;
        });

        console.log("front repo pull returned")
      }
    )
  }

  drop(event: CdkDragDrop<string[]>) {
    moveItemInArray(this.associatedVisualCircles, event.previousIndex, event.currentIndex);

    // set the order of VisualCircle instances
    let index = 0
    
    for (let visualcircle of this.associatedVisualCircles) {
      let revPointerID_Index = visualcircle[this.dialogData.ReversePointer+"_Index"]
      revPointerID_Index.Valid = true
      revPointerID_Index.Int64 = index++
    }
    console.log("after drop")
  }

  save() {

    this.associatedVisualCircles.forEach(
      visualcircle => {
        this.visualcircleService.updateVisualCircle(visualcircle)
          .subscribe(visualcircle => {
            this.visualcircleService.VisualCircleServiceChanged.next("update")
            console.log("visualcircle saved")
          });
      }
    )

    this.dialogRef.close('Sorting of '+ this.dialogData.ReversePointer+' done');
  }
}
