import { Component, OnInit, AfterViewInit, ViewChild, Inject, Optional } from '@angular/core';
import { BehaviorSubject } from 'rxjs'
import { MatSort } from '@angular/material/sort';
import { MatPaginator } from '@angular/material/paginator';
import { MatTableDataSource } from '@angular/material/table';
import { MatButton } from '@angular/material/button'

import { MatDialogRef, MAT_DIALOG_DATA, MatDialog } from '@angular/material/dialog'
import { DialogData, FrontRepoService, FrontRepo, SelectionMode } from '../front-repo.service'
import { NullInt64 } from '../null-int64'
import { SelectionModel } from '@angular/cdk/collections';

const allowMultiSelect = true;

import { Router, RouterState } from '@angular/router';
import { VisualCircleDB } from '../visualcircle-db'
import { VisualCircleService } from '../visualcircle.service'

// TableComponent is initilizaed from different routes
// TableComponentMode detail different cases 
enum TableComponentMode {
  DISPLAY_MODE,
  ONE_MANY_ASSOCIATION_MODE,
  MANY_MANY_ASSOCIATION_MODE,
}

// generated table component
@Component({
  selector: 'app-visualcirclestable',
  templateUrl: './visualcircles-table.component.html',
  styleUrls: ['./visualcircles-table.component.css'],
})
export class VisualCirclesTableComponent implements OnInit {

  // mode at invocation
  mode: TableComponentMode = TableComponentMode.DISPLAY_MODE

  // used if the component is called as a selection component of VisualCircle instances
  selection: SelectionModel<VisualCircleDB> = new (SelectionModel)
  initialSelection = new Array<VisualCircleDB>()

  // the data source for the table
  visualcircles: VisualCircleDB[] = []
  matTableDataSource: MatTableDataSource<VisualCircleDB> = new (MatTableDataSource)

  // front repo, that will be referenced by this.visualcircles
  frontRepo: FrontRepo = new (FrontRepo)

  // displayedColumns is referenced by the MatTable component for specify what columns
  // have to be displayed and in what order
  displayedColumns: string[];

  // for sorting & pagination
  @ViewChild(MatSort)
  sort: MatSort | undefined
  @ViewChild(MatPaginator)
  paginator: MatPaginator | undefined;

  ngAfterViewInit() {

    // enable sorting on all fields (including pointers and reverse pointer)
    this.matTableDataSource.sortingDataAccessor = (visualcircleDB: VisualCircleDB, property: string) => {
      switch (property) {
        // insertion point for specific sorting accessor
        case 'Lat':
          return visualcircleDB.Lat;

        case 'Lng':
          return visualcircleDB.Lng;

        case 'Name':
          return visualcircleDB.Name;

        case 'Radius':
          return visualcircleDB.Radius;

        case 'VisualColorEnum':
          return visualcircleDB.VisualColorEnum;

        case 'DashStyleEnum':
          return visualcircleDB.DashStyleEnum;

        case 'VisualLayer':
          return (visualcircleDB.VisualLayer ? visualcircleDB.VisualLayer.Name : '');

        default:
          console.assert(false, "Unknown field")
          return "";
      }
    };

    // enable filtering on all fields (including pointers and reverse pointer, which is not done by default)
    this.matTableDataSource.filterPredicate = (visualcircleDB: VisualCircleDB, filter: string) => {

      // filtering is based on finding a lower case filter into a concatenated string
      // the visualcircleDB properties
      let mergedContent = ""

      // insertion point for merging of fields
      mergedContent += visualcircleDB.Lat.toString()
      mergedContent += visualcircleDB.Lng.toString()
      mergedContent += visualcircleDB.Name.toLowerCase()
      mergedContent += visualcircleDB.Radius.toString()
      mergedContent += visualcircleDB.VisualColorEnum.toLowerCase()
      mergedContent += visualcircleDB.DashStyleEnum.toLowerCase()
      if (visualcircleDB.VisualLayer) {
        mergedContent += visualcircleDB.VisualLayer.Name.toLowerCase()
      }

      let isSelected = mergedContent.includes(filter.toLowerCase())
      return isSelected
    };

    this.matTableDataSource.sort = this.sort!
    this.matTableDataSource.paginator = this.paginator!
  }

  applyFilter(event: Event) {
    const filterValue = (event.target as HTMLInputElement).value;
    this.matTableDataSource.filter = filterValue.trim().toLowerCase();
  }

  constructor(
    private visualcircleService: VisualCircleService,
    private frontRepoService: FrontRepoService,

    // not null if the component is called as a selection component of visualcircle instances
    public dialogRef: MatDialogRef<VisualCirclesTableComponent>,
    @Optional() @Inject(MAT_DIALOG_DATA) public dialogData: DialogData,

    private router: Router,
  ) {

    // compute mode
    if (dialogData == undefined) {
      this.mode = TableComponentMode.DISPLAY_MODE
    } else {
      switch (dialogData.SelectionMode) {
        case SelectionMode.ONE_MANY_ASSOCIATION_MODE:
          this.mode = TableComponentMode.ONE_MANY_ASSOCIATION_MODE
          break
        case SelectionMode.MANY_MANY_ASSOCIATION_MODE:
          this.mode = TableComponentMode.MANY_MANY_ASSOCIATION_MODE
          break
        default:
      }
    }

    // observable for changes in structs
    this.visualcircleService.VisualCircleServiceChanged.subscribe(
      message => {
        if (message == "post" || message == "update" || message == "delete") {
          this.getVisualCircles()
        }
      }
    )
    if (this.mode == TableComponentMode.DISPLAY_MODE) {
      this.displayedColumns = ['ID', 'Edit', 'Delete', // insertion point for columns to display
        "Lat",
        "Lng",
        "Name",
        "Radius",
        "VisualColorEnum",
        "DashStyleEnum",
        "VisualLayer",
      ]
    } else {
      this.displayedColumns = ['select', 'ID', // insertion point for columns to display
        "Lat",
        "Lng",
        "Name",
        "Radius",
        "VisualColorEnum",
        "DashStyleEnum",
        "VisualLayer",
      ]
      this.selection = new SelectionModel<VisualCircleDB>(allowMultiSelect, this.initialSelection);
    }

  }

  ngOnInit(): void {
    this.getVisualCircles()
    this.matTableDataSource = new MatTableDataSource(this.visualcircles)
  }

  getVisualCircles(): void {
    this.frontRepoService.pull().subscribe(
      frontRepo => {
        this.frontRepo = frontRepo

        this.visualcircles = this.frontRepo.VisualCircles_array;

        // insertion point for variables Recoveries

        // in case the component is called as a selection component
        if (this.mode == TableComponentMode.ONE_MANY_ASSOCIATION_MODE) {
          this.visualcircles.forEach(
            visualcircle => {
              let ID = this.dialogData.ID
              let revPointer = visualcircle[this.dialogData.ReversePointer as keyof VisualCircleDB] as unknown as NullInt64
              if (revPointer.Int64 == ID) {
                this.initialSelection.push(visualcircle)
              }
            }
          )
          this.selection = new SelectionModel<VisualCircleDB>(allowMultiSelect, this.initialSelection);
        }

        if (this.mode == TableComponentMode.MANY_MANY_ASSOCIATION_MODE) {

          let mapOfSourceInstances = this.frontRepo[this.dialogData.SourceStruct + "s" as keyof FrontRepo] as Map<number, VisualCircleDB>
          let sourceInstance = mapOfSourceInstances.get(this.dialogData.ID)!

          let sourceField = sourceInstance[this.dialogData.SourceField as keyof typeof sourceInstance]! as unknown as VisualCircleDB[]
          for (let associationInstance of sourceField) {
            let visualcircle = associationInstance[this.dialogData.IntermediateStructField as keyof typeof associationInstance] as unknown as VisualCircleDB
            this.initialSelection.push(visualcircle)
          }

          this.selection = new SelectionModel<VisualCircleDB>(allowMultiSelect, this.initialSelection);
        }

        // update the mat table data source
        this.matTableDataSource.data = this.visualcircles
      }
    )
  }

  // newVisualCircle initiate a new visualcircle
  // create a new VisualCircle objet
  newVisualCircle() {
  }

  deleteVisualCircle(visualcircleID: number, visualcircle: VisualCircleDB) {
    // list of visualcircles is truncated of visualcircle before the delete
    this.visualcircles = this.visualcircles.filter(h => h !== visualcircle);

    this.visualcircleService.deleteVisualCircle(visualcircleID).subscribe(
      visualcircle => {
        this.visualcircleService.VisualCircleServiceChanged.next("delete")
      }
    );
  }

  editVisualCircle(visualcircleID: number, visualcircle: VisualCircleDB) {

  }

  // display visualcircle in router
  displayVisualCircleInRouter(visualcircleID: number) {
    this.router.navigate(["github_com_fullstack_lang_gongleaflet_go-" + "visualcircle-display", visualcircleID])
  }

  // set editor outlet
  setEditorRouterOutlet(visualcircleID: number) {
    this.router.navigate([{
      outlets: {
        github_com_fullstack_lang_gongleaflet_go_editor: ["github_com_fullstack_lang_gongleaflet_go-" + "visualcircle-detail", visualcircleID]
      }
    }]);
  }

  // set presentation outlet
  setPresentationRouterOutlet(visualcircleID: number) {
    this.router.navigate([{
      outlets: {
        github_com_fullstack_lang_gongleaflet_go_presentation: ["github_com_fullstack_lang_gongleaflet_go-" + "visualcircle-presentation", visualcircleID]
      }
    }]);
  }

  /** Whether the number of selected elements matches the total number of rows. */
  isAllSelected() {
    const numSelected = this.selection.selected.length;
    const numRows = this.visualcircles.length;
    return numSelected === numRows;
  }

  /** Selects all rows if they are not all selected; otherwise clear selection. */
  masterToggle() {
    this.isAllSelected() ?
      this.selection.clear() :
      this.visualcircles.forEach(row => this.selection.select(row));
  }

  save() {

    if (this.mode == TableComponentMode.ONE_MANY_ASSOCIATION_MODE) {

      let toUpdate = new Set<VisualCircleDB>()

      // reset all initial selection of visualcircle that belong to visualcircle
      this.initialSelection.forEach(
        visualcircle => {
          let index = visualcircle[this.dialogData.ReversePointer as keyof VisualCircleDB] as unknown as NullInt64
          index.Int64 = 0
          index.Valid = true
          toUpdate.add(visualcircle)
        }
      )

      // from selection, set visualcircle that belong to visualcircle
      this.selection.selected.forEach(
        visualcircle => {
          let ID = this.dialogData.ID as number
          let reversePointer = visualcircle[this.dialogData.ReversePointer  as keyof VisualCircleDB] as unknown as NullInt64
          reversePointer.Int64 = ID
          toUpdate.add(visualcircle)
        }
      )

      // update all visualcircle (only update selection & initial selection)
      toUpdate.forEach(
        visualcircle => {
          this.visualcircleService.updateVisualCircle(visualcircle)
            .subscribe(visualcircle => {
              this.visualcircleService.VisualCircleServiceChanged.next("update")
            });
        }
      )
    }

    if (this.mode == TableComponentMode.MANY_MANY_ASSOCIATION_MODE) {

      // get the source instance via the map of instances in the front repo
      let mapOfSourceInstances = this.frontRepo[this.dialogData.SourceStruct + "s" as keyof FrontRepo] as Map<number, VisualCircleDB>
      let sourceInstance = mapOfSourceInstances.get(this.dialogData.ID)!

      // First, parse all instance of the association struct and remove the instance
      // that have unselect
      let unselectedVisualCircle = new Set<number>()
      for (let visualcircle of this.initialSelection) {
        if (this.selection.selected.includes(visualcircle)) {
          // console.log("visualcircle " + visualcircle.Name + " is still selected")
        } else {
          console.log("visualcircle " + visualcircle.Name + " has been unselected")
          unselectedVisualCircle.add(visualcircle.ID)
          console.log("is unselected " + unselectedVisualCircle.has(visualcircle.ID))
        }
      }

      // delete the association instance
      let associationInstance = sourceInstance[this.dialogData.SourceField as keyof typeof sourceInstance]
      let visualcircle = associationInstance![this.dialogData.IntermediateStructField as keyof typeof associationInstance] as unknown as VisualCircleDB
      if (unselectedVisualCircle.has(visualcircle.ID)) {
        this.frontRepoService.deleteService(this.dialogData.IntermediateStruct, associationInstance)


      }

      // is the source array is empty create it
      if (sourceInstance[this.dialogData.SourceField as keyof typeof sourceInstance] == undefined) {
        (sourceInstance[this.dialogData.SourceField as keyof typeof sourceInstance] as unknown as Array<VisualCircleDB>) = new Array<VisualCircleDB>()
      }

      // second, parse all instance of the selected
      if (sourceInstance[this.dialogData.SourceField as keyof typeof sourceInstance]) {
        this.selection.selected.forEach(
          visualcircle => {
            if (!this.initialSelection.includes(visualcircle)) {
              // console.log("visualcircle " + visualcircle.Name + " has been added to the selection")

              let associationInstance = {
                Name: sourceInstance["Name"] + "-" + visualcircle.Name,
              }

              let index = associationInstance[this.dialogData.IntermediateStructField+"ID" as keyof typeof associationInstance] as unknown as NullInt64
              index.Int64 = visualcircle.ID

              let indexDB = associationInstance[this.dialogData.IntermediateStructField+"DBID" as keyof typeof associationInstance] as unknown as NullInt64
              indexDB.Int64 = visualcircle.ID

              this.frontRepoService.postService( this.dialogData.IntermediateStruct, associationInstance )

            } else {
              // console.log("visualcircle " + visualcircle.Name + " is still selected")
            }
          }
        )
      }

      // this.selection = new SelectionModel<VisualCircleDB>(allowMultiSelect, this.initialSelection);
    }

    // why pizza ?
    this.dialogRef.close('Pizza!');
  }
}
