// generated by gong
import { Component, OnInit, AfterViewInit, ViewChild, Inject, Optional } from '@angular/core';
import { BehaviorSubject } from 'rxjs'
import { MatSort } from '@angular/material/sort';
import { MatPaginator } from '@angular/material/paginator';
import { MatTableDataSource } from '@angular/material/table';
import { MatButton } from '@angular/material/button'

import { MatDialogRef, MAT_DIALOG_DATA, MatDialog } from '@angular/material/dialog'
import { DialogData, FrontRepoService, FrontRepo, SelectionMode } from '../front-repo.service'
import { NullInt64 } from '../null-int64'
import { SelectionModel } from '@angular/cdk/collections';

const allowMultiSelect = true;

import { Router, RouterState } from '@angular/router';
import { VisualIconDB } from '../visualicon-db'
import { VisualIconService } from '../visualicon.service'

// TableComponent is initilizaed from different routes
// TableComponentMode detail different cases 
enum TableComponentMode {
  DISPLAY_MODE,
  ONE_MANY_ASSOCIATION_MODE,
  MANY_MANY_ASSOCIATION_MODE,
}

// generated table component
@Component({
  selector: 'app-visualiconstable',
  templateUrl: './visualicons-table.component.html',
  styleUrls: ['./visualicons-table.component.css'],
})
export class VisualIconsTableComponent implements OnInit {

  // mode at invocation
  mode: TableComponentMode = TableComponentMode.DISPLAY_MODE

  // used if the component is called as a selection component of VisualIcon instances
  selection: SelectionModel<VisualIconDB> = new (SelectionModel)
  initialSelection = new Array<VisualIconDB>()

  // the data source for the table
  visualicons: VisualIconDB[] = []
  matTableDataSource: MatTableDataSource<VisualIconDB> = new (MatTableDataSource)

  // front repo, that will be referenced by this.visualicons
  frontRepo: FrontRepo = new (FrontRepo)

  // displayedColumns is referenced by the MatTable component for specify what columns
  // have to be displayed and in what order
  displayedColumns: string[];

  // for sorting & pagination
  @ViewChild(MatSort)
  sort: MatSort | undefined
  @ViewChild(MatPaginator)
  paginator: MatPaginator | undefined;

  ngAfterViewInit() {

    // enable sorting on all fields (including pointers and reverse pointer)
    this.matTableDataSource.sortingDataAccessor = (visualiconDB: VisualIconDB, property: string) => {
      switch (property) {
        // insertion point for specific sorting accessor
        case 'Name':
          return visualiconDB.Name;

        case 'SVG':
          return visualiconDB.SVG;

        default:
          console.assert(false, "Unknown field")
          return "";
      }
    };

    // enable filtering on all fields (including pointers and reverse pointer, which is not done by default)
    this.matTableDataSource.filterPredicate = (visualiconDB: VisualIconDB, filter: string) => {

      // filtering is based on finding a lower case filter into a concatenated string
      // the visualiconDB properties
      let mergedContent = ""

      // insertion point for merging of fields
      mergedContent += visualiconDB.Name.toLowerCase()
      mergedContent += visualiconDB.SVG.toLowerCase()

      let isSelected = mergedContent.includes(filter.toLowerCase())
      return isSelected
    };

    this.matTableDataSource.sort = this.sort!
    this.matTableDataSource.paginator = this.paginator!
  }

  applyFilter(event: Event) {
    const filterValue = (event.target as HTMLInputElement).value;
    this.matTableDataSource.filter = filterValue.trim().toLowerCase();
  }

  constructor(
    private visualiconService: VisualIconService,
    private frontRepoService: FrontRepoService,

    // not null if the component is called as a selection component of visualicon instances
    public dialogRef: MatDialogRef<VisualIconsTableComponent>,
    @Optional() @Inject(MAT_DIALOG_DATA) public dialogData: DialogData,

    private router: Router,
  ) {

    // compute mode
    if (dialogData == undefined) {
      this.mode = TableComponentMode.DISPLAY_MODE
    } else {
      switch (dialogData.SelectionMode) {
        case SelectionMode.ONE_MANY_ASSOCIATION_MODE:
          this.mode = TableComponentMode.ONE_MANY_ASSOCIATION_MODE
          break
        case SelectionMode.MANY_MANY_ASSOCIATION_MODE:
          this.mode = TableComponentMode.MANY_MANY_ASSOCIATION_MODE
          break
        default:
      }
    }

    // observable for changes in structs
    this.visualiconService.VisualIconServiceChanged.subscribe(
      message => {
        if (message == "post" || message == "update" || message == "delete") {
          this.getVisualIcons()
        }
      }
    )
    if (this.mode == TableComponentMode.DISPLAY_MODE) {
      this.displayedColumns = ['ID', 'Edit', 'Delete', // insertion point for columns to display
        "Name",
        "SVG",
      ]
    } else {
      this.displayedColumns = ['select', 'ID', // insertion point for columns to display
        "Name",
        "SVG",
      ]
      this.selection = new SelectionModel<VisualIconDB>(allowMultiSelect, this.initialSelection);
    }

  }

  ngOnInit(): void {
    this.getVisualIcons()
    this.matTableDataSource = new MatTableDataSource(this.visualicons)
  }

  getVisualIcons(): void {
    this.frontRepoService.pull().subscribe(
      frontRepo => {
        this.frontRepo = frontRepo

        this.visualicons = this.frontRepo.VisualIcons_array;

        // insertion point for variables Recoveries

        // in case the component is called as a selection component
        if (this.mode == TableComponentMode.ONE_MANY_ASSOCIATION_MODE) {
          for (let visualicon of this.visualicons) {
            let ID = this.dialogData.ID
            let revPointer = visualicon[this.dialogData.ReversePointer as keyof VisualIconDB] as unknown as NullInt64
            if (revPointer.Int64 == ID) {
              this.initialSelection.push(visualicon)
            }
            this.selection = new SelectionModel<VisualIconDB>(allowMultiSelect, this.initialSelection);
          }
        }

        if (this.mode == TableComponentMode.MANY_MANY_ASSOCIATION_MODE) {

          let mapOfSourceInstances = this.frontRepo[this.dialogData.SourceStruct + "s" as keyof FrontRepo] as Map<number, VisualIconDB>
          let sourceInstance = mapOfSourceInstances.get(this.dialogData.ID)!

          let sourceField = sourceInstance[this.dialogData.SourceField as keyof typeof sourceInstance]! as unknown as VisualIconDB[]
          for (let associationInstance of sourceField) {
            let visualicon = associationInstance[this.dialogData.IntermediateStructField as keyof typeof associationInstance] as unknown as VisualIconDB
            this.initialSelection.push(visualicon)
          }

          this.selection = new SelectionModel<VisualIconDB>(allowMultiSelect, this.initialSelection);
        }

        // update the mat table data source
        this.matTableDataSource.data = this.visualicons
      }
    )
  }

  // newVisualIcon initiate a new visualicon
  // create a new VisualIcon objet
  newVisualIcon() {
  }

  deleteVisualIcon(visualiconID: number, visualicon: VisualIconDB) {
    // list of visualicons is truncated of visualicon before the delete
    this.visualicons = this.visualicons.filter(h => h !== visualicon);

    this.visualiconService.deleteVisualIcon(visualiconID).subscribe(
      visualicon => {
        this.visualiconService.VisualIconServiceChanged.next("delete")
      }
    );
  }

  editVisualIcon(visualiconID: number, visualicon: VisualIconDB) {

  }

  // display visualicon in router
  displayVisualIconInRouter(visualiconID: number) {
    this.router.navigate(["github_com_fullstack_lang_gongleaflet_go-" + "visualicon-display", visualiconID])
  }

  // set editor outlet
  setEditorRouterOutlet(visualiconID: number) {
    this.router.navigate([{
      outlets: {
        github_com_fullstack_lang_gongleaflet_go_editor: ["github_com_fullstack_lang_gongleaflet_go-" + "visualicon-detail", visualiconID]
      }
    }]);
  }

  // set presentation outlet
  setPresentationRouterOutlet(visualiconID: number) {
    this.router.navigate([{
      outlets: {
        github_com_fullstack_lang_gongleaflet_go_presentation: ["github_com_fullstack_lang_gongleaflet_go-" + "visualicon-presentation", visualiconID]
      }
    }]);
  }

  /** Whether the number of selected elements matches the total number of rows. */
  isAllSelected() {
    const numSelected = this.selection.selected.length;
    const numRows = this.visualicons.length;
    return numSelected === numRows;
  }

  /** Selects all rows if they are not all selected; otherwise clear selection. */
  masterToggle() {
    this.isAllSelected() ?
      this.selection.clear() :
      this.visualicons.forEach(row => this.selection.select(row));
  }

  save() {

    if (this.mode == TableComponentMode.ONE_MANY_ASSOCIATION_MODE) {

      let toUpdate = new Set<VisualIconDB>()

      // reset all initial selection of visualicon that belong to visualicon
      for (let visualicon of this.initialSelection) {
        let index = visualicon[this.dialogData.ReversePointer as keyof VisualIconDB] as unknown as NullInt64
        index.Int64 = 0
        index.Valid = true
        toUpdate.add(visualicon)

      }

      // from selection, set visualicon that belong to visualicon
      for (let visualicon of this.selection.selected) {
        let ID = this.dialogData.ID as number
        let reversePointer = visualicon[this.dialogData.ReversePointer as keyof VisualIconDB] as unknown as NullInt64
        reversePointer.Int64 = ID
        reversePointer.Valid = true
        toUpdate.add(visualicon)
      }


      // update all visualicon (only update selection & initial selection)
      for (let visualicon of toUpdate) {
        this.visualiconService.updateVisualIcon(visualicon)
          .subscribe(visualicon => {
            this.visualiconService.VisualIconServiceChanged.next("update")
          });
      }
    }

    if (this.mode == TableComponentMode.MANY_MANY_ASSOCIATION_MODE) {

      // get the source instance via the map of instances in the front repo
      let mapOfSourceInstances = this.frontRepo[this.dialogData.SourceStruct + "s" as keyof FrontRepo] as Map<number, VisualIconDB>
      let sourceInstance = mapOfSourceInstances.get(this.dialogData.ID)!

      // First, parse all instance of the association struct and remove the instance
      // that have unselect
      let unselectedVisualIcon = new Set<number>()
      for (let visualicon of this.initialSelection) {
        if (this.selection.selected.includes(visualicon)) {
          // console.log("visualicon " + visualicon.Name + " is still selected")
        } else {
          console.log("visualicon " + visualicon.Name + " has been unselected")
          unselectedVisualIcon.add(visualicon.ID)
          console.log("is unselected " + unselectedVisualIcon.has(visualicon.ID))
        }
      }

      // delete the association instance
      let associationInstance = sourceInstance[this.dialogData.SourceField as keyof typeof sourceInstance]
      let visualicon = associationInstance![this.dialogData.IntermediateStructField as keyof typeof associationInstance] as unknown as VisualIconDB
      if (unselectedVisualIcon.has(visualicon.ID)) {
        this.frontRepoService.deleteService(this.dialogData.IntermediateStruct, associationInstance)


      }

      // is the source array is empty create it
      if (sourceInstance[this.dialogData.SourceField as keyof typeof sourceInstance] == undefined) {
        (sourceInstance[this.dialogData.SourceField as keyof typeof sourceInstance] as unknown as Array<VisualIconDB>) = new Array<VisualIconDB>()
      }

      // second, parse all instance of the selected
      if (sourceInstance[this.dialogData.SourceField as keyof typeof sourceInstance]) {
        this.selection.selected.forEach(
          visualicon => {
            if (!this.initialSelection.includes(visualicon)) {
              // console.log("visualicon " + visualicon.Name + " has been added to the selection")

              let associationInstance = {
                Name: sourceInstance["Name"] + "-" + visualicon.Name,
              }

              let index = associationInstance[this.dialogData.IntermediateStructField + "ID" as keyof typeof associationInstance] as unknown as NullInt64
              index.Int64 = visualicon.ID
              index.Valid = true

              let indexDB = associationInstance[this.dialogData.IntermediateStructField + "DBID" as keyof typeof associationInstance] as unknown as NullInt64
              indexDB.Int64 = visualicon.ID
              index.Valid = true

              this.frontRepoService.postService(this.dialogData.IntermediateStruct, associationInstance)

            } else {
              // console.log("visualicon " + visualicon.Name + " is still selected")
            }
          }
        )
      }

      // this.selection = new SelectionModel<VisualIconDB>(allowMultiSelect, this.initialSelection);
    }

    // why pizza ?
    this.dialogRef.close('Pizza!');
  }
}
