// generated by gong
import { Component, OnInit, AfterViewInit, ViewChild, Inject, Optional } from '@angular/core';
import { BehaviorSubject } from 'rxjs'
import { MatSort } from '@angular/material/sort';
import { MatPaginator } from '@angular/material/paginator';
import { MatTableDataSource } from '@angular/material/table';
import { MatButton } from '@angular/material/button'

import { MatDialogRef, MAT_DIALOG_DATA, MatDialog } from '@angular/material/dialog'
import { DialogData, FrontRepoService, FrontRepo, SelectionMode } from '../front-repo.service'
import { NullInt64 } from '../null-int64'
import { SelectionModel } from '@angular/cdk/collections';

const allowMultiSelect = true;

import { Router, RouterState } from '@angular/router';
import { VisualLayerDB } from '../visuallayer-db'
import { VisualLayerService } from '../visuallayer.service'

// TableComponent is initilizaed from different routes
// TableComponentMode detail different cases 
enum TableComponentMode {
  DISPLAY_MODE,
  ONE_MANY_ASSOCIATION_MODE,
  MANY_MANY_ASSOCIATION_MODE,
}

// generated table component
@Component({
  selector: 'app-visuallayerstable',
  templateUrl: './visuallayers-table.component.html',
  styleUrls: ['./visuallayers-table.component.css'],
})
export class VisualLayersTableComponent implements OnInit {

  // mode at invocation
  mode: TableComponentMode = TableComponentMode.DISPLAY_MODE

  // used if the component is called as a selection component of VisualLayer instances
  selection: SelectionModel<VisualLayerDB> = new (SelectionModel)
  initialSelection = new Array<VisualLayerDB>()

  // the data source for the table
  visuallayers: VisualLayerDB[] = []
  matTableDataSource: MatTableDataSource<VisualLayerDB> = new (MatTableDataSource)

  // front repo, that will be referenced by this.visuallayers
  frontRepo: FrontRepo = new (FrontRepo)

  // displayedColumns is referenced by the MatTable component for specify what columns
  // have to be displayed and in what order
  displayedColumns: string[];

  // for sorting & pagination
  @ViewChild(MatSort)
  sort: MatSort | undefined
  @ViewChild(MatPaginator)
  paginator: MatPaginator | undefined;

  ngAfterViewInit() {

    // enable sorting on all fields (including pointers and reverse pointer)
    this.matTableDataSource.sortingDataAccessor = (visuallayerDB: VisualLayerDB, property: string) => {
      switch (property) {
        // insertion point for specific sorting accessor
        case 'Name':
          return visuallayerDB.Name;

        case 'DisplayName':
          return visuallayerDB.DisplayName;

        default:
          console.assert(false, "Unknown field")
          return "";
      }
    };

    // enable filtering on all fields (including pointers and reverse pointer, which is not done by default)
    this.matTableDataSource.filterPredicate = (visuallayerDB: VisualLayerDB, filter: string) => {

      // filtering is based on finding a lower case filter into a concatenated string
      // the visuallayerDB properties
      let mergedContent = ""

      // insertion point for merging of fields
      mergedContent += visuallayerDB.Name.toLowerCase()
      mergedContent += visuallayerDB.DisplayName.toLowerCase()

      let isSelected = mergedContent.includes(filter.toLowerCase())
      return isSelected
    };

    this.matTableDataSource.sort = this.sort!
    this.matTableDataSource.paginator = this.paginator!
  }

  applyFilter(event: Event) {
    const filterValue = (event.target as HTMLInputElement).value;
    this.matTableDataSource.filter = filterValue.trim().toLowerCase();
  }

  constructor(
    private visuallayerService: VisualLayerService,
    private frontRepoService: FrontRepoService,

    // not null if the component is called as a selection component of visuallayer instances
    public dialogRef: MatDialogRef<VisualLayersTableComponent>,
    @Optional() @Inject(MAT_DIALOG_DATA) public dialogData: DialogData,

    private router: Router,
  ) {

    // compute mode
    if (dialogData == undefined) {
      this.mode = TableComponentMode.DISPLAY_MODE
    } else {
      switch (dialogData.SelectionMode) {
        case SelectionMode.ONE_MANY_ASSOCIATION_MODE:
          this.mode = TableComponentMode.ONE_MANY_ASSOCIATION_MODE
          break
        case SelectionMode.MANY_MANY_ASSOCIATION_MODE:
          this.mode = TableComponentMode.MANY_MANY_ASSOCIATION_MODE
          break
        default:
      }
    }

    // observable for changes in structs
    this.visuallayerService.VisualLayerServiceChanged.subscribe(
      message => {
        if (message == "post" || message == "update" || message == "delete") {
          this.getVisualLayers()
        }
      }
    )
    if (this.mode == TableComponentMode.DISPLAY_MODE) {
      this.displayedColumns = ['ID', 'Edit', 'Delete', // insertion point for columns to display
        "Name",
        "DisplayName",
      ]
    } else {
      this.displayedColumns = ['select', 'ID', // insertion point for columns to display
        "Name",
        "DisplayName",
      ]
      this.selection = new SelectionModel<VisualLayerDB>(allowMultiSelect, this.initialSelection);
    }

  }

  ngOnInit(): void {
    this.getVisualLayers()
    this.matTableDataSource = new MatTableDataSource(this.visuallayers)
  }

  getVisualLayers(): void {
    this.frontRepoService.pull().subscribe(
      frontRepo => {
        this.frontRepo = frontRepo

        this.visuallayers = this.frontRepo.VisualLayers_array;

        // insertion point for variables Recoveries

        // in case the component is called as a selection component
        if (this.mode == TableComponentMode.ONE_MANY_ASSOCIATION_MODE) {
          this.visuallayers.forEach(
            visuallayer => {
              let ID = this.dialogData.ID
              let revPointer = visuallayer[this.dialogData.ReversePointer as keyof VisualLayerDB] as unknown as NullInt64
              if (revPointer.Int64 == ID) {
                this.initialSelection.push(visuallayer)
              }
            }
          )
          this.selection = new SelectionModel<VisualLayerDB>(allowMultiSelect, this.initialSelection);
        }

        if (this.mode == TableComponentMode.MANY_MANY_ASSOCIATION_MODE) {

          let mapOfSourceInstances = this.frontRepo[this.dialogData.SourceStruct + "s" as keyof FrontRepo] as Map<number, VisualLayerDB>
          let sourceInstance = mapOfSourceInstances.get(this.dialogData.ID)!

          let sourceField = sourceInstance[this.dialogData.SourceField as keyof typeof sourceInstance]! as unknown as VisualLayerDB[]
          for (let associationInstance of sourceField) {
            let visuallayer = associationInstance[this.dialogData.IntermediateStructField as keyof typeof associationInstance] as unknown as VisualLayerDB
            this.initialSelection.push(visuallayer)
          }

          this.selection = new SelectionModel<VisualLayerDB>(allowMultiSelect, this.initialSelection);
        }

        // update the mat table data source
        this.matTableDataSource.data = this.visuallayers
      }
    )
  }

  // newVisualLayer initiate a new visuallayer
  // create a new VisualLayer objet
  newVisualLayer() {
  }

  deleteVisualLayer(visuallayerID: number, visuallayer: VisualLayerDB) {
    // list of visuallayers is truncated of visuallayer before the delete
    this.visuallayers = this.visuallayers.filter(h => h !== visuallayer);

    this.visuallayerService.deleteVisualLayer(visuallayerID).subscribe(
      visuallayer => {
        this.visuallayerService.VisualLayerServiceChanged.next("delete")
      }
    );
  }

  editVisualLayer(visuallayerID: number, visuallayer: VisualLayerDB) {

  }

  // display visuallayer in router
  displayVisualLayerInRouter(visuallayerID: number) {
    this.router.navigate(["github_com_fullstack_lang_gongleaflet_go-" + "visuallayer-display", visuallayerID])
  }

  // set editor outlet
  setEditorRouterOutlet(visuallayerID: number) {
    this.router.navigate([{
      outlets: {
        github_com_fullstack_lang_gongleaflet_go_editor: ["github_com_fullstack_lang_gongleaflet_go-" + "visuallayer-detail", visuallayerID]
      }
    }]);
  }

  // set presentation outlet
  setPresentationRouterOutlet(visuallayerID: number) {
    this.router.navigate([{
      outlets: {
        github_com_fullstack_lang_gongleaflet_go_presentation: ["github_com_fullstack_lang_gongleaflet_go-" + "visuallayer-presentation", visuallayerID]
      }
    }]);
  }

  /** Whether the number of selected elements matches the total number of rows. */
  isAllSelected() {
    const numSelected = this.selection.selected.length;
    const numRows = this.visuallayers.length;
    return numSelected === numRows;
  }

  /** Selects all rows if they are not all selected; otherwise clear selection. */
  masterToggle() {
    this.isAllSelected() ?
      this.selection.clear() :
      this.visuallayers.forEach(row => this.selection.select(row));
  }

  save() {

    if (this.mode == TableComponentMode.ONE_MANY_ASSOCIATION_MODE) {

      let toUpdate = new Set<VisualLayerDB>()

      // reset all initial selection of visuallayer that belong to visuallayer
      this.initialSelection.forEach(
        visuallayer => {
          let index = visuallayer[this.dialogData.ReversePointer as keyof VisualLayerDB] as unknown as NullInt64
          index.Int64 = 0
          index.Valid = true
          toUpdate.add(visuallayer)
        }
      )

      // from selection, set visuallayer that belong to visuallayer
      this.selection.selected.forEach(
        visuallayer => {
          let ID = this.dialogData.ID as number
          let reversePointer = visuallayer[this.dialogData.ReversePointer  as keyof VisualLayerDB] as unknown as NullInt64
          reversePointer.Int64 = ID
          toUpdate.add(visuallayer)
        }
      )

      // update all visuallayer (only update selection & initial selection)
      toUpdate.forEach(
        visuallayer => {
          this.visuallayerService.updateVisualLayer(visuallayer)
            .subscribe(visuallayer => {
              this.visuallayerService.VisualLayerServiceChanged.next("update")
            });
        }
      )
    }

    if (this.mode == TableComponentMode.MANY_MANY_ASSOCIATION_MODE) {

      // get the source instance via the map of instances in the front repo
      let mapOfSourceInstances = this.frontRepo[this.dialogData.SourceStruct + "s" as keyof FrontRepo] as Map<number, VisualLayerDB>
      let sourceInstance = mapOfSourceInstances.get(this.dialogData.ID)!

      // First, parse all instance of the association struct and remove the instance
      // that have unselect
      let unselectedVisualLayer = new Set<number>()
      for (let visuallayer of this.initialSelection) {
        if (this.selection.selected.includes(visuallayer)) {
          // console.log("visuallayer " + visuallayer.Name + " is still selected")
        } else {
          console.log("visuallayer " + visuallayer.Name + " has been unselected")
          unselectedVisualLayer.add(visuallayer.ID)
          console.log("is unselected " + unselectedVisualLayer.has(visuallayer.ID))
        }
      }

      // delete the association instance
      let associationInstance = sourceInstance[this.dialogData.SourceField as keyof typeof sourceInstance]
      let visuallayer = associationInstance![this.dialogData.IntermediateStructField as keyof typeof associationInstance] as unknown as VisualLayerDB
      if (unselectedVisualLayer.has(visuallayer.ID)) {
        this.frontRepoService.deleteService(this.dialogData.IntermediateStruct, associationInstance)


      }

      // is the source array is empty create it
      if (sourceInstance[this.dialogData.SourceField as keyof typeof sourceInstance] == undefined) {
        (sourceInstance[this.dialogData.SourceField as keyof typeof sourceInstance] as unknown as Array<VisualLayerDB>) = new Array<VisualLayerDB>()
      }

      // second, parse all instance of the selected
      if (sourceInstance[this.dialogData.SourceField as keyof typeof sourceInstance]) {
        this.selection.selected.forEach(
          visuallayer => {
            if (!this.initialSelection.includes(visuallayer)) {
              // console.log("visuallayer " + visuallayer.Name + " has been added to the selection")

              let associationInstance = {
                Name: sourceInstance["Name"] + "-" + visuallayer.Name,
              }

              let index = associationInstance[this.dialogData.IntermediateStructField+"ID" as keyof typeof associationInstance] as unknown as NullInt64
              index.Int64 = visuallayer.ID

              let indexDB = associationInstance[this.dialogData.IntermediateStructField+"DBID" as keyof typeof associationInstance] as unknown as NullInt64
              indexDB.Int64 = visuallayer.ID

              this.frontRepoService.postService( this.dialogData.IntermediateStruct, associationInstance )

            } else {
              // console.log("visuallayer " + visuallayer.Name + " is still selected")
            }
          }
        )
      }

      // this.selection = new SelectionModel<VisualLayerDB>(allowMultiSelect, this.initialSelection);
    }

    // why pizza ?
    this.dialogRef.close('Pizza!');
  }
}
